mapper.select:
SELECT * FROM goods WHERE 1=1
AND title LIKE concat('%', #{title} ,'%')
AND shop = #{shop}

    AND locate(#{condition},title) = 0

AND etl_date in (

    #{day}

)

group by shop,etl_date

select * from goods  a
inner join(
    select max(price) as price from goods where title like concat('%',#{goodName},'%') and etl_date = #{now}
    and shop in

        #{secondShop.name}



        AND locate(#{condition},title) = 0

)b
on a.price = b.price
where 1=1
and a.etl_date = #{now}
and shop in

    #{secondShop.name}

and title like concat('%',#{goodName},'%')

select * from goods a
inner join (
select min(price) as price from goods where title like concat('%',#{goodName},'%') and etl_date = #{now}


    AND locate(#{condition},title) = 0







)b
on a.price = b.price
where 1=1
AND title like concat('%',#{goodName},'%')
and etl_date = #{now}
and shop not in

    #{blackShop.name}
